type FieldValidator map[string]map[string]*FieldInformation

func (f FieldValidator) ValidField(table string, field string) bool {
	if tb, ok := f[table]; ok {
		_,ok = tb[field]
		return ok
	}
	return false
}

func (f FieldValidator) ValidTable(table string) bool {
	_, ok := f[table]
	return ok
}

